mapper.insert:

		SELECT MP_BOARD_SEQ.NEXTVAL FROM DUAL	   


   INSERT INTO MP_BOARD(  
   							BNO, PARENT_BNO, DEPTH, SORT
   					      , TITLE 
   					      , CONTENT 
   					      , WRITER
   					      , PW  )
                 VALUES(  
                 			#{bno}
                 		  , #{parent_bno}
                 		  , #{depth}
                 		  , ( select (nvl(max(sort), 0)+10) as sort 
							  from mp_board 
							  where parent_bno = #{parent_bno} ) 
                 		  , #{title}
                 		  , #{content}
                 		  , #{writer}
                 		  , #{pw}  )

mapper.select:
SELECT T4.*, NVL(U.bno2, 0) as bno2, (CASE WHEN mf.bno > 0 THEN 'Y' ELSE 'N' END) as file_yn FROM ( SELECT T3.*, ROWNUM as RRNUM FROM ( SELECT CASE WHEN T2.DELETE_YN = 'Y' AND T2.PARENT_BNO = 0 THEN '=== 원글이 삭제되었습니다. ===' ELSE T2.TITLE END AS TITLE_TEMP , T2.*
FROM ( SELECT T1.TITLE, T1.BNO, T1.PARENT_BNO, T1.SORT, T1.DEPTH, T1.CONTENT, T1.WRITER, T1.REGDATE, RNUM, DELETE_YN, (select count(*) from mp_reply s1 where s1.bno = t1.bno) reply_cnt
FROM ( SELECT TITLE, BNO, PARENT_BNO, SORT, DEPTH, CONTENT, WRITER, REGDATE, DELETE_YN, ROW_NUMBER() OVER(ORDER BY BNO) AS RNUM, (select count(*) from mp_reply s1 where s1.bno = bno) reply_cnt FROM MP_BOARD
) T1
START WITH T1.PARENT_BNO = 0 CONNECT BY PRIOR T1.BNO = T1.PARENT_BNO ORDER SIBLINGS BY SORT DESC ) T2 ) T3 WHERE 1=1
) T4 LEFT JOIN ( SELECT bno, ROW_NUMBER() OVER (ORDER BY bno ASC) as bno2 FROM mp_board WHERE parent_bno = 0 ) U on U.bno = T4.bno LEFT JOIN (SELECT DISTINCT bno FROM mp_file) mf on mf.bno = T4.bno WHERE T4.RRNUM BETWEEN #{rowStart} AND #{rowEnd}

mapper.update:
UPDATE MP_BOARD
   SET TITLE    =   #{title},
   	   CONTENT  =   #{content}
 WHERE BNO = #{bno}

mapper.delete:
UPDATE MP_BOARD SET
 DELETE_YN = 'Y'
 WHERE BNO = #{bno}

mapper.select:
SELECT PW
	FROM MP_BOARD
WHERE BNO = #{bno}

mapper.update:
UPDATE MP_FILE SET
DEL_GB = 'Y'
WHERE FILE_NO = #{FILE_NO}

mapper.select:
SELECT T3.* FROM ( SELECT CASE WHEN T2.DELETE_YN = 'Y' AND T2.PARENT_BNO = 0 THEN '=== 원글이 삭제되었습니다. ===' ELSE T2.TITLE END AS TITLE_TEMP , T2.* , ROWNUM AS RRNUM
FROM ( SELECT T1.TITLE, T1.BNO, T1.PARENT_BNO, T1.SORT, T1.DEPTH, T1.CONTENT, T1.WRITER, T1.REGDATE, RNUM, DELETE_YN, (select count(*) from mp_reply s1 where s1.bno = t1.bno) reply_cnt, ROWNUM as ORDERNUM
FROM ( SELECT TITLE, BNO, PARENT_BNO, SORT, DEPTH, CONTENT, WRITER, REGDATE, DELETE_YN, ROW_NUMBER() OVER(ORDER BY BNO) AS RNUM, (select count(*) from mp_reply s1 where s1.bno = bno) reply_cnt FROM MP_BOARD
) T1
START WITH T1.PARENT_BNO = 0 CONNECT BY PRIOR T1.BNO = T1.PARENT_BNO ORDER SIBLINGS BY SORT DESC ) T2 WHERE 1=1
) T3
